settings.custom_style_css:
.product-card-price.m-final .price {
    font-weight: 700 !important;
    letter-spacing: 0.1rem;
    font-size: 1.4rem;
}
/*-----------RODAPÉ-----------*/
.footer-top {
    padding-top: 4rem !important;
    padding-bottom: 4rem !important;
    padding: 8rem 0 !important;
    -webkit-box-shadow: 0px 0px 40px 0px rgba(0, 0, 0, 0.15);
    -moz-box-shadow: 0px 0px 40px 0px rgba(0, 0, 0, 0.15);
    box-shadow: 0px 0px 40px 0px rgba(0, 0, 0, 0.15);
}
.footer-middle {
    background: #F8F8F8;
}
.footer-top,
.footer-middle {
    border-bottom: 0 !important;
}
.footer-logo.version p,
.footer-logo.version h1,
.footer-logo.version a {
    color: #808080 !important;
}
.footer-logo {
    border-top: solid .1rem #EBEBEB !important;
}
.footer-3 .footer-newsletter .footer-submit {
    border-radius: 0 !important;
    display: flex;
    align-items: center;
    justify-content: center;
}
.footer-3 .footer-newsletter .footer-submit .text {
    text-indent: 0;
}
/*-----------CARRINHO-----------*/
.header .header-box .nav-cart .nav-icon, .header .header-top .nav-cart .nav-icon {
    color: var(--header-cart);
    display: none !important;

/*-----------filtro-----------*/
.collection-sidebar .sidebar-block--price {
    display: none !important;
}
